import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { SanityService } from 'src/app/core/services/sanity.service';
import { toHTML, uriLooksSafe } from '@portabletext/to-html';
import htm from 'htm';
import vhtml from 'vhtml';

@Component({
  selector: 'dd-news',
  templateUrl: './news.component.html',
})
export class NewsComponent {
  articleSlug: string = '';
  post: any;
  imagesLoaded: boolean = false;

  html = htm.bind(vhtml);
  myPortableTextComponents = {
    types: {},

    marks: {
      link: ({ children, value }: any) => {
        const href = value.href || '';
        if (uriLooksSafe(href)) {
          const rel = href.startsWith('/') ? undefined : 'noreferrer noopener';
          return this.html`<a href="${href}" rel="${rel}">${children}</a>`;
        }
        return children;
      },
    },
  };

  constructor(
    private sanityService: SanityService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(async (params) => {
      this.articleSlug = params.get('slug') || '';
      this.post = await this.sanityService.getPostBySlug(this.articleSlug);
      this.post.author = await this.sanityService.getAuthorByRefence(
        this.post.author._ref
      );
      this.post.mainImage = await this.sanityService.urlFor(
        this.post.mainImage
      );
      this.post.author.image.asset = await this.sanityService.urlFor(
        this.post.author.image.asset
      );
      this.imagesLoaded = true;
    });
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    const day = ('0' + date.getDate()).slice(-2);
    const month = ('0' + (date.getMonth() + 1)).slice(-2);
    const year = date.getFullYear();

    return `${day}/${month}/${year}`;
  }

  toHTML(text: any) {
    return toHTML(text, { components: this.myPortableTextComponents });
  }
}

import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { SanityService } from 'src/app/core/services/sanity.service';

@Component({
  selector: 'dd-news-container',
  templateUrl: './news-container.component.html',
})
export class NewsContainerComponent implements OnChanges {
  @Input() category?: string;
  posts: any;

  constructor(private sanityService: SanityService) {}

  ngOnInit() {
    this.updateNews();
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['category'] && !changes['category'].firstChange) {
      // The category input has changed. Update the component.
      this.updateNews();
    }
  }

  updateNews(): void {
    if (this.category) {
      this.sanityService.getPostsByCategory(this.category).then((posts) => {
        this.posts = this.groupPostsByDate(posts);
      });
    } else {
      this.sanityService.getPosts().then((posts) => {
        this.posts = this.groupPostsByDate(posts);
      });
    }
  }

  groupPostsByDate(posts: any) {
    const groupedPosts = posts.reduce((grouped: any, post: any) => {
      const date = new Date(post.publishedAt).toLocaleDateString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      });
      if (!grouped[date]) {
        grouped[date] = [];
      }
      grouped[date].push(post);
      return grouped;
    }, {});

    return Object.entries(groupedPosts);
  }
}

import { Component, OnInit, inject } from '@angular/core';
